package Lab;

public class GreatestOtTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//find greatest of two using ternary operator
		int num1=1,num2=2;
		String str=" ";
		str=(num1>num2)?num1 +" is greatest":num2+" is greatest";
		System.out.println(str);
		
	}

}
