package String;

public class String_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Length Method
					String Str="Hitendra";
					System.out.println("Length of String is : "+Str.length());
					
				// charAt(index)  method
					String string_1="Chaudhari";
					char charact=string_1.charAt(4);
					System.out.println(string_1.charAt(3));
					System.out.println("Character at index  : "+charact);
	}

}
